INSERT INTO `sanpham` (`MASP`, `TENSP`, `GIA`, `KHUYENMAI`, `ANHLON`, `ANHNHO`, `THONGTIN`, `SOLUONG`, `MALOAISP`, `MATHUONGHIEU`, `MANV`, `LUOTMUA`, `LUOTTHICH`, `LUOTXEM`) VALUES
(1, 'Apple Iphone 6s Plus 16GB', '13960000', 19, '/hinhanh/hinhsanpham/iphone6spluse.jpg', '/hinhanh/hinhsanpham/iphone6spluse1.jpg,/hinhanh/hinhsanpham/iphone6spluse2.jpg,/hinhanh/hinhsanpham/iphone6spluse3.png,/hinhanh/hinhsanpham/iphone6spluse4.jpg', 'Là hàng được nhập khẩu trực tiếp từ nước ngoài bởi doanh nghiệp trong nước, không thông qua nhà phân phối chính thức tại thị trường Việt Nam.\r\nHàng nhập khẩu được nhiều người chọn lựa bởi giá thành tốt, chất lượng vẫn được đảm bảo như những sản phẩm được nhập khẩu thông qua nhà phân phối chính thức (vì được sản xuất từ cùng một nhà máy của hãng sản xuất). Hơn nữa, dù không được bảo hành tại các trung tâm bảo hành chính thức của hãng, các sản phẩm này vẫn được áp dụng đầy đủ chính sách bảo hành của doanh nghiệp nhập khẩu', 5, 8, 1, 1, 3, 5, 1),
(2, 'Pin sạc dự phòng Asus Zenpower 10.050mAh (Vàng) ', '339000', 19, '/hinhanh/hinhsanpham/pinsacduphongasus.jpg', '/hinhanh/hinhsanpham/pinsacduphongasus.jpg', 'Asus ZenPower là viên pin sạc chính hãng hiếm hoi đến từ một nhà sản xuất điện thoại khá lớn ở Việt Nam. Hiện tại ZenPower có khá nhiều màu khác nhau, từ hồng đẹp trai đến màu đen yếu đuối. Hiện tại thì mình mượn được màu hồng mạnh mẽ và vàng sang chảnh để trên tay. Asus có bán các miếng bảo vệ cao su nhiều màu cho pin nhưng không rõ có được phân phối ở Việt Nam hay không.', 10, 8, 5, 2, 2, 6, 1),
(3, 'Bao da kiêm bàn phím cho máy tính bảng 7 inch ( Đen)  ', '98000', 19, '/hinhanh/hinhsanpham/baodabanphimmaytinhbang7ich.jpg', '', 'Asus ZenPower là viên pin sạc chính hãng hiếm hoi đến từ một nhà sản xuất điện thoại khá lớn ở Việt Nam. Hiện tại ZenPower có khá nhiều màu khác nhau, từ hồng đẹp trai đến màu đen yếu đuối. Hiện tại thì mình mượn được màu hồng mạnh mẽ và vàng sang chảnh để trên tay. Asus có bán các miếng bảo vệ cao su nhiều màu cho pin nhưng không rõ có được phân phối ở Việt Nam hay không.', 10, 8, 5, 2, 5, 11, 1),
(4, 'Gậy chụp hình Selfie Stick (Đen phối vàng) ', '36000', 13, '/hinhanh/hinhsanpham/gaychuphinhtusuong.png', '', 'Asus ZenPower là viên pin sạc chính hãng hiếm hoi đến từ một nhà sản xuất điện thoại khá lớn ở Việt Nam. Hiện tại ZenPower có khá nhiều màu khác nhau, từ hồng đẹp trai đến màu đen yếu đuối. Hiện tại thì mình mượn được màu hồng mạnh mẽ và vàng sang chảnh để trên tay. Asus có bán các miếng bảo vệ cao su nhiều màu cho pin nhưng không rõ có được phân phối ở Việt Nam hay không.', 10, 8, 5, 2, 9, 15, 0),
