(609, 'VỢT CẦU LÔNG YONEX CACBON - VỢT CẦU LÔNG YONEX CACBON\r\n', '450000', 45, '/hinhanh/hinhsanpham/votcaulongyonexcacbon.jpg', '/hinhanh/hinhsanpham/votcaulongyonexcacbon1.jpg,/hinhanh/hinhsanpham/votcaulongyonexcacbon2.jpg,/hinhanh/hinhsanpham/votcaulongyonexcacbon3.jpg,/hinhanh/hinhsanpham/votcaulongyonexcacbon4.jpg,/hinhanh/hinhsanpham/votcaulongyonexcacbon5.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nHãng sản xuất: Yonex.\r\nCHI TIẾT SẢN PHẨM\r\nTHÔNG TIN VỢT CẦU LÔNG YONEX CACBON\r\n+ Trên thị trường có rất nhiều loại yonex khung cacbon nhưng vợt của shop là loại tốt nhất của dòng yonex khung các bạn nhé!\r\n\r\nKhi mua vợt bạn sẽ được TẶNG:\r\n+ Tặng 01 sợi dây cước căng sẵn trên khung vợt trị giá 80k ( Hãy inbox cho shop Lực căng cước bạn đang chơi nhé! )\r\n+ Tặng 01 cuốn cán vợt V S chính hãng chống trơn trị giá 10k\r\n+ Tặng 01 bao đựng vợt chống sốc mới 100% theo vợt\r\n\r\n THÔNG TIN VỢT CẦU LÔNG KHUNG CACBON\r\n- Sản xuất từ các sợi carbon có độ đàn hồi tốt - siêu cứng - siêu bền với công nghệ tối ưu hóa khung vợt dưới dạng khí động lực học.\r\n- Màu sắc đa dạng, nước sơn đẹp - bền\r\n- Lực căng dây tối đa: 10.5kg\r\n- Thân vợt cân bằng công tốt thủ ổn định và đặc biệt có trợ lực ( Phù hợp với cả trẻ em hay học sinh tập huấn cầu lông)\r\n- Trọng lượng W79±3g\r\n- Điểm cân bằng 298±4mm\r\n- Chiều dài 675mm\r\n- Phù hợp với người mới chơi, với người trình độ trung bình, cho người tập luyện\r\n- Chất liệu: Carbon\r\nVợt cầu lông khung cacbon (Shop giao hàng mầu ngẫu nhiên hoặc inbox cho shop để đặt màu và mức cân bạn thích nhé)\r\n\r\n', 150, 6, 6, 6, 15, 0, 0),
(610, 'Set 2 cây vợt đánh cầu lông loại tốt niềng đúc rất đẹp - 2 cây vợt Haotian\r\n', '159000', 0, '/hinhanh/hinhsanpham/set2votcaulongloaitot.jpg', '/hinhanh/hinhsanpham/set2votcaulongloaitot1.jpg,/hinhanh/hinhsanpham/set2votcaulongloaitot2.jpg,/hinhanh/hinhsanpham/set2votcaulongloaitot3.jpg,/hinhanh/hinhsanpham/set2votcaulongloaitot4.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nHãng sản xuất: Đang cập nhật.\r\nXuất xứ: Trung Quốc.', 150, 6, 6, 6, 20, 13, 0),
(611, 'Vợt cầu lông KAWASAKI 1360 - KAWASAKI 1360\r\n', '1050000', 39, '/hinhanh/hinhsanpham/votcaulongkawasaki.jpg', '/hinhanh/hinhsanpham/votcaulongkawasaki1.jpg,/hinhanh/hinhsanpham/votcaulongkawasaki2.jpg,/hinhanh/hinhsanpham/votcaulongkawasaki3.jpg,/hinhanh/hinhsanpham/votcaulongkawasaki4.jpg,/hinhanh/hinhsanpham/votcaulongkawasaki5.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nHãng sản xuất: Kawasaki.\r\nCHI TIẾT SẢN PHẨM\r\nƯU ĐÃI KHI MUA Vợt cầu lông KAWASAKI 1360\r\n–          Bảo hành lỗi  đổi mới\r\n\r\n–          Phân Phối hàng chính hãng\r\n\r\n-           Tặng 01 Bao vợt + 01 cuốn cán vợt\r\n\r\nĐẶC ĐIỂM NỔI BẬT Vợt cầu lông KAWASAKI 1360\r\nVợt cầu lông KAWASAKI 1360\r\nSản phẩm được bảo hành trong thời gian 60 ngày kể từ ngày mua\r\n\r\nCây vơt với lối đánh tân công mạnh mẽ, tạt trái tay dứt điểm, trên lưới hiệu quả cao. Vợt có độ cứng tring bình, dành cho người chơi lực cổ tay trung bình khá.\r\n\r\nđiểm cân bằng 2958+-3mm\r\n\r\nThân mềm đánh công thủ toàn diện\r\n\r\nTrọng lượng 4UG5\r\n\r\nLực căng 9-13kg\r\n\r\nTIÊU CHÍ TLSport\r\n\r\n–          Luôn hướng tới là đơn vị hàng đầu cung cấp thiết bị dụng cụ thể dục, thể thao, thể hình uy tín và chuyên nghiệp nhất. Vì thế chúng tôi luôn cam kết bán hàng chính hãng từ kho  đến tận tay người tiêu dùng với chi phí thấp nhất.\r\n\r\n–          Chúng tôi làm việc theo tiêu chí chữ \"Tâm\" và \" Tín\" với phương châm phục vụ quý  khách hàng \" Sức khỏe của bạn là niềm vui của chúng tôi\"', 150, 6, 6, 6, 12, 10, 0),
(612, 'Cầu Lông Hải Yến - CLhY\r\n', '106000', 0, '/hinhanh/hinhsanpham/caulonghaiyen.jpg', '', 'THUỘC TÍNH SẢN PHẨM\r\nHãng sản xuất: Đang cập nhật.\r\nXuất xứ: Việt Nam.\r\nCHI TIẾT SẢN PHẨM\r\nHải Yến là thương hiệu chuyên dành trong thí đấu đánh trong sân và ngoài trời luôn nha khách nha. Cầu Lông được thiết kế lông rất dày và mịn, đầu của trái cầu nặng giúp cầu bay đi rát đầm và đúng hướng. Rất thích hợp dành cho tập luyện và thi đấu Shop mình bán 2 loại:- Cầu 6 trái Bạc ngắn- Cầu 12 trái', 150, 6, 6, 6, 15, 10, 0),
(613, 'Vợt cầu lông proace luyện tập - PROACE\r\n', '200000', 41, '/hinhanh/hinhsanpham/votcaulongproaceluyentap.jpg', '/hinhanh/hinhsanpham/votcaulongproaceluyentap1.jpg,/hinhanh/hinhsanpham/votcaulongproaceluyentap2.jpg,/hinhanh/hinhsanpham/votcaulongproaceluyentap3.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nHãng sản xuất: Proace.\r\nXuất xứ: Trung Quốc.\r\nCHI TIẾT SẢN PHẨM\r\n*Vợt căng sẵn lưới, có bao đựng đi kèm tiện lợi\r\n\r\n*Sự lựa chọn tối ưu và tiết kiệm cho trẻ em , người mới chơi\r\n\r\n*Dành cho học sinh tập luyện tại trường học.\r\n\r\n * Không đòi hỏi lựa chọn một cây vợt yêu cầu cao như người chơi thuần thục. Cây vợt giá rẻ khoảng một mấy trăm ngàn/cây dành người mới chơi học sinh, sinh viên hay sử dụng nhiều.\r\n\r\nPhù hợp cho người mới chơi và bán chuyên\r\nChất liệu bền đẹp\r\nKiểu dáng thể thao năng động\r\nVợt đã có cước căng sẵn lưới và có bao đựng đi kèm tiện lợi.\r\nVợt cầu lông proace tập luyện là sự lựa chọn tối ưu và tiết kiệm cho người tập chơi và chơi cầu lông phong trào.\r\n\r\n', 150, 6, 6, 6, 12, 10, 0),
(614, 'Vợt cầu lông loại Vợt Cân Bằng Dunlop APEX PRO LITE+ G1 HL - 190102DBFAPEXPROLITE+G1HL\r\n', '1050000', 0, '/hinhanh/hinhsanpham/votcanbangdunlop.jpg\r\n', '/hinhanh/hinhsanpham/votcanbangdunlop1.jpg,/hinhanh/hinhsanpham/votcanbangdunlop2.jpg,/hinhanh/hinhsanpham/votcanbangdunlop3.jpg,/hinhanh/hinhsanpham/votcanbangdunlop4.jpg\r\n\r\n\r\n\r\n', 'THUỘC TÍNH SẢN PHẨM\r\nHãng sản xuất: Đang cập nhật.\r\nXuất xứ: Trung Quốc.\r\nCHI TIẾT SẢN PHẨM\r\nVợt cầu lông Dunlop APEX PRO LITE+ là dòng vợt cao cấp mới, có khung vợt rất cứng cáp:\r\n\r\n\r\nVợt cầu lông Dunlop APEX PRO LITE+ là dòng vợt Cân bằng linh hoạt, có điểm cân bằng ở giữa phù hợp với nhiều lối chơi khác nhau của người dùng.\r\n\r\nDunlop APEX PRO LITE+ có độ dẻo rất linh hoạt, khung vợt chắc chắn, chịu được lực đánh mạnh sẽ giữ vợt luôn ổn định khi nhận những quả cầu cực mạnh.\r\n\r\nKhung và cán được làm bằng chất liệu HM6 Carbon siêu bền. có tay cầm vừa vặn giúp cầm nắm vừa tay.\r\n\r\n\r\n\r\nThông số Vợt cầu lông Dunlop APEX PRO LITE+:\r\nCấu tạo: HM6 Carbon\r\n\r\nChiều dài: 675mm\r\n\r\nTrọng lượng: 81g+-2\r\n\r\nBalance: Even (linh hoạt)\r\n\r\nFlex: Extra Flexible\r\n\r\nLực căng dây tối đa: 30Ibs/14kg\r\n\r\n', 150, 6, 6, 6, 16, 10, 0),
(615, 'Vợt cầu lông cao cấp PROACE - PRA42\r\n', '450000', 0, '/hinhanh/hinhsanpham/votcaulongcaocapproace.jpg', '/hinhanh/hinhsanpham/votcaulongcaocapproace1.jpg,/hinhanh/hinhsanpham/votcaulongcaocapproace2.jpg,/hinhanh/hinhsanpham/votcaulongcaocapproace3.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nHãng sản xuất: Proace.\r\nXuất xứ: Anh.\r\nCHI TIẾT SẢN PHẨM\r\nVợt cầu lông Proace\r\nCHI TIẾT SẢN PHẨM\r\nSản phẩm vợt cầu lông Proace Sweetspot \r\n1. Thông số vợt cầu lông Proace Sweetspot \r\n- Mã vợt: Sweetspot \r\n\r\n- Hãng sản xuất: Proace.\r\n\r\n- Trọng lượng vợt cầu lông: 84 ± 2 g.\r\n\r\n- Size vợt: 3U - G2.\r\n\r\n- Vợt cầu lông Proace Sweetspot 950 cho phép người chơi tập trung nhiều hơn vào các kỹ thuật và tốc độ được phát huy tối đa. Vợt thể hiện một lối đánh tấn công mạnh mẽ và đầy sức mạnh. Độ cân bằng và ổn định mặt vợt cực tốt thể hiện ngay trong từng động tác.\r\n\r\n- Mua vợt cầu lông tại Hoàng đức  Sport để được khuyến mại thêm cuốn cán ,bao vợt.và căng dây sẵn 10kg cho người chơi\r\n\r\n ', 150, 6, 6, 6, 12, 4, 0);
