(776, 'Hũ Đựng Gia Vị Kèm Muỗng Cực Xịn - hugiavi\r\n', '55000', 0, '/hinhanh/hinhsanpham/hudunggiavikemmuong.jpg', '/hinhanh/hinhsanpham/hudunggiavikemmuong1.jpg,/hinhanh/hinhsanpham/hudunggiavikemmuong2.jpg,/hinhanh/hinhsanpham/hudunggiavikemmuong3.jpg,/hinhanh/hinhsanpham/hudunggiavikemmuong4.jpg,/hinhanh/hinhsanpham/hudunggiavikemmuong5.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Thủy tinh.\r\nCHI TIẾT SẢN PHẨM\r\n HŨ ĐỰNG GIA VỊ KÈM MUỖNG CỰC XỊN ?\r\n BỘ ĐỰNG GIA VỊ CỰC XINH CHO CĂN BẾP HIỆN ĐẠI\r\n HŨ GIA VỊ KÈM SẴN MUỖNG, PHẾT SIÊU XINH, SẠCH SẼ\r\n Chất liệu : THỦY TINH & NHỰA SẦN \r\n\r\n Chất lượng hoàn thiện rất tốt\r\n\r\n Hũ đựng gia vị kèm muỗng sử dụng siêu tiện lựi\r\n\r\n\r\n Với bộ đựng gia vị nhỏ xinh này, góc bếp gia đình trở nên đẹp mắt và gọn gàng hẳn lên\r\n Những chiếc lọ mang kiểu dáng hình trụ với đường cong mềm mại, được làm bằng thủy tinh cao cấp, trong suốt, giúp bạn thấy gia vị chứa ở bên trong và dễ dàng nhìn lấy ra sử dụng\r\n Đặc biệt, lọ gia vị được thiết kế kèm sẵn muỗng, phết giúp việc lấy gia vị trở nên dễ dàng, sạch sẽ\r\n HŨ ĐỰNG GIA VỊ KÈM MUỖNG có Vòng cao su quanh miệng nắp giúp đậy chặt, tránh côn trùng bụi bẩn...', 150, 8, 8, 8, 40, 19, 0),
(777, '[Hỗ trợ 10k ship] Set 5 l ọ thủy tinh đựng gia vị tiện dụng - Bộ 5 lọ đựng Gia vị\r\n', '85000', 0, '/hinhanh/hinhsanpham/set5lothuytinhdunggiavi.jpg', '/hinhanh/hinhsanpham/set5lothuytinhdunggiavi1.jpg,/hinhanh/hinhsanpham/set5lothuytinhdunggiavi2.jpg,/hinhanh/hinhsanpham/set5lothuytinhdunggiavi3.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Thủy tinh.\r\nCHI TIẾT SẢN PHẨM\r\nSet 5 l ọ  th ủy ti nh đựng gia vị tiện dụng\r\nGia vị là những nguyên liệu nấu ăn không thể thiếu trong căn bếp của mọi gia đình. Để bảo quản an toàn cho các loại gia vị, bạn cần sử dụng các hũ thủy tinh cao cấp, thay cho những chiếc hũ nhựa thông thường bởi thủy tinh không chứa các chất độc hại gây ảnh hưởng đến sức khỏe của người dùng. Set 5 l  ọ th ủy ti nh đựng gia vị tiện dụng sẽ là sự lựa chọn lý tưởng cho chị em nội trợ .\r\n\r\n\r\n\r\nSet 5  l ọ th ủy ti nh đựng gia vị\r\n\r\n– Lọ đựng gia vị làm bằng chất liệu thủy tinh, nắp nhựa an toàn, không chứa chất độc hại, không phai mầu gây ảnh hưởng đến sức khỏe người tiêu dùng.\r\n– Lọ đựng gia vị có mầu sắc hài hòa, họa tiết trang nhã, tạo cảm giác dễ chịu.\r\n– Kiểu dáng đẹp, sạch sẽ, gọn gàng, an toàn, phù hợp cho mọi không gian nhà bếp.\r\n– Thủy tinh trong giúp bạn phân biệt được từng loại gia vị.\r\n– Sản phẩm gọn nhẹ, tháo lắp dễ dàng để vệ sinh tẩy rửa.\r\n– Thích hợp làm quà tặng cho các chị em yêu thích nội trợ.', 150, 8, 8, 8, 23, 10, 0),
(778, 'Vịt mắm 2 cái - 2 lọ mắm thủy tinh 100 ml - VM100ml\r\n', '100000', 12, '/hinhanh/hinhsanpham/2lomamthuytinh.jpg', '/hinhanh/hinhsanpham/2lomamthuytinh1.jpg,/hinhanh/hinhsanpham/2lomamthuytinh2.jpg,/hinhanh/hinhsanpham/2lomamthuytinh3.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Thủy tinh.\r\nXuất xứ: Indonesia.\r\nCHI TIẾT SẢN PHẨM\r\nBộ gồm 2 vịt mắm làm bằng thủy tinh 100 ml.\r\nVịt mắm cao 10 cm.\r\nLọ thủy tinh có vòi, nắp nhựa..Phù hợp để nước mắm trên bàn ăn.', 150, 8, 8, 8, 42, 32, 0),
(779, '[Trợ ship 10k] Bộ 5 hộp bảo quản thực phẩm trong tủ lạnh Tashuan TS-3179 - TS-3179\r\n', '100000', 40, '/hinhanh/hinhsanpham/bo5hopbaoquanthucpham.jpg', '/hinhanh/hinhsanpham/bo5hopbaoquanthucpham1.jpg,/hinhanh/hinhsanpham/bo5hopbaoquanthucpham2.jpg,/hinhanh/hinhsanpham/bo5hopbaoquanthucpham3.jpg,/hinhanh/hinhsanpham/bo5hopbaoquanthucpham4.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Nhựa.\r\nXuất xứ: Việt Nam.\r\nCHI TIẾT SẢN PHẨM\r\nBộ 5 hộp đựng thực phẩm để trong tủ lạnh Tashuan TS3179 được thiết kế kiểu dáng hình chữ nhật vừa nhỏ gọn lại vừa tiện dụng đựng các loại thực phẩm, rau củ, trái cây cắt nhỏ... giúp cho bạn giữ được nguyên vị tươi ngon, xanh sạch khi để trong tủ lạnh. Bộ hộp đều được làm bằng chất liệu nhựa an toàn không gây độc hại cho thực phẩm, bền đẹp và chắc chắn, dễ chùi rửa, cho giá trị sử dụng lâu dài. Với thiết kế hộp có nắp đậy kín giúp bảo quản thực phẩm tươi ngon, mùi thực phẩm không bay ra ngoài thích hợp cho việc bảo quản thực phẩm trong tủ lạnh, tủ đông. Sản phẩm giúp tiết kiệm không gian cho tủ lạnh nhà bạn khi lưu trữ thực phẩm.\r\n\r\n', 150, 8, 8, 8, 23, 10, 0),
(780, 'Bộ 5 hũ đựng gia vị bằng thủy tinh cao cấp - Bộ 5 hũ đựng gia vị\r\n', '90000', 0, '/hinhanh/hinhsanpham/bo5hudunggiavibangthuytinh.jpg', '/hinhanh/hinhsanpham/bo5hudunggiavibangthuytinh1.jpg,/hinhanh/hinhsanpham/bo5hudunggiavibangthuytinh2.jpg,/hinhanh/hinhsanpham/bo5hudunggiavibangthuytinh3.jpg,/hinhanh/hinhsanpham/bo5hudunggiavibangthuytinh4.jpg,/hinhanh/hinhsanpham/bo5hudunggiavibangthuytinh5.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Thủy tinh.\r\nCHI TIẾT SẢN PHẨM\r\nBộ 5 hũ đựng gia vị bằng thủy tinh cao cấp\r\nGia vị là những nguyên liệu nấu ăn không thể thiếu trong căn bếp của mọi gia đình. Để bảo quản an toàn cho các loại gia vị, thay cho những chiếc hũ nhựa thông thường tiềm ẩn nhiều chất độc hại gây ảnh hưởng đến sức khỏe của người dùng. Hôm nay, Dientugiadung giới thiệu với các bạn bộ hũ thủy tinh cao cấp, không chứa các chất độ hại đảm bảo sức khỏe gia đình, người thân của bạn. Với nắp đậy kín và chắc chắn, những chiếc hũ này sẽ bảo đảm an toàn cho phần gia vị bên trong, tránh chảy nước hay mất mùi gia vị.\r\n\r\n', 150, 8, 8, 8, 12, 2, 0),
(781, 'Bộ 5 Hộp Đựng Thực Phẩm Tủ Lạnh Tashuan - GD00952TS.01\r\n', '90000', 22, '/hinhanh/hinhsanpham/bo5hopdungthucpham.jpg', '/hinhanh/hinhsanpham/bo5hopdungthucpham1.jpg,/hinhanh/hinhsanpham/bo5hopdungthucpham2.jpg,/hinhanh/hinhsanpham/bo5hopdungthucpham3.jpg,/hinhanh/hinhsanpham/bo5hopdungthucpham4.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Nhựa.\r\nXuất xứ: Việt Nam.\r\nCHI TIẾT SẢN PHẨM\r\nBộ 5 Hộp Đựng Thực Phẩm Tủ Lạnh Tashuan\r\n\r\nĐiểm nổi bật:\r\n\r\n- Bộ 5 Hộp Đựng Thực Phẩm Tashuan là sản phẩm chính hãng của Công ty nhựa Tashuan hàng đầu Việt Nam, sản xuất theo công nghệ Đài Loan và tiêu chuẩn Quốc tế, đảm bảo chất lượng và vệ sinh an toàn cho người tiêu dùng.\r\n\r\n\r\n\r\n- Thiết kế nhỏ gọn, kiểu dáng hình chữ nhật hiện đại, chất liệu nhựa bền đẹp, không màu, không mùi, không hóa chất độc hại, sản phẩm còn kèm theo một chiếc khay nhựa tiện lợi có tay cầm tiện lợi, dễ dàng di chuyển và sử dụng, giúp tiết kiệm không gian và bảo quản thức ăn luôn tươi mới, tránh ẩm mốc và vi khuẩn xâm nhập.\r\n\r\n\r\n\r\n- Sản phẩm có nắp đậy giúp bảo quản thực phẩm hiệu quả, giữ được mùi vị và độ tươi ngon của các loại trái cây, rau củ như cà chua, dưa leo, cà rốt, hành lá,… hoặc cũng có để dùng để đựng các thực phẩm khô như nui, các loại đậu, ngoài ra còn được sử dụng như hộp đựng gia vị nhà bếp.', 150, 8, 8, 8, 29, 21, 0),
(782, 'Combo 2 thố đựng mứt - Thố đựng mứt kẹo - Lọ thủy tinh có nắp đậy - Combo 2 thố đựng mứt\r\n', '99000', 0, '/hinhanh/hinhsanpham/combo2thodungmut.jpg', '/hinhanh/hinhsanpham/combo2thodungmut1.jpg,/hinhanh/hinhsanpham/combo2thodungmut2.jpg,/hinhanh/hinhsanpham/combo2thodungmut3.jpg,/hinhanh/hinhsanpham/combo2thodungmut4.jpg,/hinhanh/hinhsanpham/combo2thodungmut5.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Thủy tinh.\r\nXuất xứ: Trung Quốc.\r\nCHI TIẾT SẢN PHẨM\r\nTHÔNG TIN SẢN PHẨM\r\n\r\nThố được làm bằng chất liệu thủy tinh trong suốt\r\nCó nắp đậy khít giúp bạn yên tâm khi bảo quản thực phẩm\r\nKhông gây hại cho sức khỏe\r\nKiểu dáng sang trọng và đẹp mắt\r\nThích hợp dùng để đựng bánh, mứt... để trang trí cho nhà bạn nhân dịp tết năm nay\r\nCAM KẾT\r\n\r\n- SHOP đảm bảo bán hàng giống hình 100%\r\n\r\n- Tất cả các sản phẩm được kiểm tra kỹ trước khi bán\r\n\r\n- Giao hàng và thanh toán tại nhà\r\n\r\n- Luôn cam kết chất lượng và giá cạnh tranh\r\n\r\n- Đổi trả hàng nếu lỗi của nhà sản xuất', 150, 8, 8, 8, 23, 10, 0),
(783, 'Bộ 3 bát sứ có nắp hút chân không không tai - Bát sứ không tai\r\n', '125000', 45, '/hinhanh/hinhsanpham/bo3batsuconaphutchankhong.jpg', '/hinhanh/hinhsanpham/bo3batsuconaphutchankhong1.jpg,/hinhanh/hinhsanpham/bo3batsuconaphutchankhong2.jpg,/hinhanh/hinhsanpham/bo3batsuconaphutchankhong3.jpg,/hinhanh/hinhsanpham/bo3batsuconaphutchankhong4.jpg,/hinhanh/hinhsanpham/bo3batsuconaphutchankhong5.jpg,/hinhanh/hinhsanpham/bo3batsuconaphutchankhong6.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Sứ.\r\nCHI TIẾT SẢN PHẨM\r\nBộ 3 bát sứ có nắp hút chân không không tai\r\nTại sao chọn mua sản phẩm bộ 3 bát sứ có nắp hút chân không?\r\n\r\n- Chất liệu cao cấp an toàn sử dụng: bát được làm từ gốm sứ cao cấp giúp thức ăn không bị mất mùi do quá trình cất giữ lâu trong tủ lạnh hay không bị ảnh hưởng do quá trình nung nóng bởi lò vi sóng. Đặc biệt bộ bát sứ còn có nắp đậy được làm từ nhựa và cao su an toàn tuyệt đối cho sức khỏe được thiết kế đóng chặt với thành bát, hạn chế sự tiếp xúc với không khí bên ngoài ngăn không cho vi khuẩn xâm nhập.\r\n\r\n- Thiết kế hoa văn tinh tế bắt mắt: sản phẩm được đưa đến với người tiêu dùng không chỉ chất lượng về chất liệu còn hài hòa về cả “ nước sơn” giúp tô điểm cho phòng bếp nhà bạn thêm sang trọng đặc biệt tăng thêm độ hấp dãn cho các món ăn.\r\n\r\n- Tính ứng dụng sản phẩm cao: sản phẩm với 3 kích cỡ sẽ vừa là những chiếc bát xinh xắn cho bữa cơm ngon miệng , khi “ rảnh” lại có thể là những hộp đựng bảo quản thức ăn trong tủ lạnh và cũng có thể biến thành hộp giúp bạn mang thức ăn mọi lúc mọi nơi rất tiện.Tính năng chịu được nhiệt độ lên đến 140 độ C nên bạn yên tâm sử dụng sản phẩm này để nấu thức ăn trong lò vi sóng thật tiện dụng\r\n\r\n- Dễ dàng vệ sinh sản phẩm: với bề mặt bát được tráng men láng mịn sáng bóng  nên sẽ rất khó cho những vết bẩn hay dầu mỡ có thể bám lại được, chỉ cần sử dụng bông rửa nhẹ nhàng không cần kì cọ vất vả như nhiều sản phẩm thông thường khác.', 150, 8, 8, 8, 24, 15, 0),
(784, 'Hộp thủy tinh LOCK&LOCK 1000ml - 0M4\r\n', '85000', 0, '/hinhanh/hinhsanpham/hopthuytinhlockandlock.jpg', '/hinhanh/hinhsanpham/hopthuytinhlockandlock1.jpg,/hinhanh/hinhsanpham/hopthuytinhlockandlock2.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Thủy tinh.\r\nXuất xứ: Việt Nam.\r\nCHI TIẾT SẢN PHẨM\r\nHộp vuông thuỷ tinh 1000ml lock&lock Xuất xứ: Lock&Lock Việt Nam \r\n\r\nNắp bằng nhựa PP(-20 độ C~120 độ C) Vành bằng Sillicon (-40 độ C~200 độ C) Thân làm từ thuỷ tinh chịu nhiệt, khả năng chịu nhiệt lên đến 400 độ C Lưu ý khi sử dụng : Không dùng giẻ lau, đồ chùi bằng nhôm hay bằng kim loại có bề mặt cứng, thô ráp sẽ gây trầy xước bề mặt sản phẩm. Sử dụng giẻ hoặc miếng xốp mềm cùng với chất tẩy rửa nhẹ để lau chùi. Khi lau rửa, không nên để lẫn hộp bằng thủy tinh và các vật dụng có chứa kim loại khác. Nên tách hộp bằng thủy tinh để chùi rửa riêng. Không dùng muỗng đũa hoặc vật cứng để cạy gỡ thức ăn dính sẽ dễ gây rạn, nứt làm hư hại sản phẩm. Bảo quản tránh xa tầm với của trẻ em. Tháo rời nắp khi sử dụng trong lò vi sóng, máy rửa chén. Không đun nóng trực tiếp trên lửa.\r\n\r\n', 150, 8, 8, 8, 56, 14, 0),
(785, 'Bộ 5 bát INOX có nắp đậy - bát inox - bát inox có nắp - Thố inox - An toàn cho sức khỏe - Bộ 5 bát INOX có nắp đậy\r\n', '130000', 32, '/hinhanh/hinhsanpham/bo5batinoxconapday.jpg', '/hinhanh/hinhsanpham/bo5batinoxconapday1.jpg,/hinhanh/hinhsanpham/bo5batinoxconapday2.jpg,/hinhanh/hinhsanpham/bo5batinoxconapday3.jpg,/hinhanh/hinhsanpham/bo5batinoxconapday4.jpg,/hinhanh/hinhsanpham/bo5batinoxconapday5.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Inox.\r\nXuất xứ: Trung Quốc.\r\nCHI TIẾT SẢN PHẨM\r\nThông tin sản phẩm\r\n\r\n Bộ 5 hộp đựng thực phẩm inox  nhiều kích thước, cho bạn thoải mái bảo quản thực phẩm.\r\n Nắp nhựa an toàn dễ dàng cất giữ thức ăn\r\n Làm bằng inox cao cấp, không gây hại cho sức khỏe\r\n Kiểu dáng đẹp, dễ dàng chùi rửa và không bị bám mùi\r\n Đường kính mỗi chiếc là: 10, 12, 14, 16, 18 cm\r\n Chất liệu đặc trưng hợp kim, không ăn mòn, không gỉ,\r\n Bảo quản thực phẩm mà không gây ra mùi khó chịu khi mở tủ lạnh.\r\n Giữ được vệ sinh an toàn thực phẩm cho cả nhà\r\n \r\nCAM KẾT\r\n\r\n- SHOP đảm bảo bán hàng giống hình 100%\r\n\r\n- Tất cả các sản phẩm được kiểm tra kỹ trước khi bán\r\n\r\n- Giao hàng và thanh toán tại nhà\r\n\r\n- Luôn cam kết chất lượng và giá cạnh tranh\r\n\r\n- Đổi trả hàng nếu lỗi của nhà sản xuất\r\n\r\n', 150, 8, 8, 8, 18, 5, 0),
(786, 'Bộ 5 thố trắng đựng mứt cao 14cm rộng 10cm - Thố đựng mứt - Thố đựng kẹo - Bộ 5 thố trắng đựng mứt\r\n', '230000', 23, '/hinhanh/hinhsanpham/bo5thodungmuc.jpg', '/hinhanh/hinhsanpham/bo5thodungmuc1.jpg,/hinhanh/hinhsanpham/bo5thodungmuc2.jpg,/hinhanh/hinhsanpham/bo5thodungmuc3.jpg\r\n', 'THÔNG TIN SẢN PHẨM\r\n\r\nThố được làm bằng chất liệu thủy tinh trong suốt\r\nCó nắp đậy khít giúp bạn yên tâm khi bảo quản thực phẩm\r\nKhông gây hại cho sức khỏe\r\nKiểu dáng sang trọng và đẹp mắt\r\nThích hợp dùng để đựng bánh, mứt... để trang trí cho nhà bạn nhân dịp tết năm nay\r\nCAM KẾT\r\n\r\n- SHOP đảm bảo bán hàng giống hình 100%\r\n\r\n- Tất cả các sản phẩm được kiểm tra kỹ trước khi bán\r\n\r\n- Giao hàng và thanh toán tại nhà\r\n\r\n- Luôn cam kết chất lượng và giá cạnh tranh\r\n\r\n- Đổi trả hàng nếu lỗi của nhà sản xuất', 150, 8, 8, 8, 52, 16, 0),
(787, 'Bộ 12 hủ đựng sữa chua thủy tinh - 12SC\r\n', '75000', 23, '/hinhanh/hinhsanpham/bo12hudungsuachua.jpg', '/hinhanh/hinhsanpham/bo12hudungsuachua1.jpg,/hinhanh/hinhsanpham/bo12hudungsuachua2.jpg,/hinhanh/hinhsanpham/bo12hudungsuachua3.jpg', 'THUỘC TÍNH SẢN PHẨM\r\nChất liệu: Thủy tinh.\r\nXuất xứ: Việt Nam.\r\nCHI TIẾT SẢN PHẨM\r\nBộ 12 hủ sữa chua thủy tinh. Hình kitty rất dễ thương. An toàn tiện lợi cho mọi gia đình\r\n\r\nDung tích : 100ml\r\n\r\nIn nhiều hình khác nhau tùy theo lô hàng như: kitty, trâu, bò,... rất ngộ nghĩnh\r\n\r\nCombo gồm 12 hủ sữa chua kèm hộp có nắp họa tiết dễ thương. Sản phẩm làm bằng thủy tinh cao cấp, khó bể, kiểu dáng xinh xắn. Họa tiết hoạt hình rất dễ thương, phù hợp vois sở thích của trẻ nhỏ Dung tích 100ml\r\n\r\nBộ 12 hủ đựng sữa chua thủy tinh\r\n\r\nBộ 12 hủ đựng sữa chua thủy tinh', 150, 8, 8, 8, 45, 39, 0),
